var submitForm=document.getElementById("submitForm"),required=document.querySelectorAll(".required");submitForm.addEventListener("click",function(){for(var e=0;e<required.length;e++)required[e].value||(required[e].classList.add("error"),window.scrollTo(required[e]))});for(var i=0;i<required.length;i++)required[i].addEventListener("input",function(){this.classList.remove("error")});